SELECT A.ORDERS_NO, A.PRODUCT_CODE, A.PRODUCT_NAME, A.ORDERS_PRICE, A.ORDERS_DATE, A.ORDERS_DIV FROM ( SELECT ROWNUM RNUM, D.ORDERS_NO, D.MEMBERS_BUYER, D.ORDERS_PRICE, D.ORDERS_DATE, D.ORDERS_DIV, D.SELLINGORDER_NO, D.MEMBERS_SELLER, D.ORDERS_STATUS, D.BUYING_ORDER_CODE , D.PRODUCT_NAME , D.PRODUCT_CODE FROM ( SELECT E.ORDERS_NO, E.MEMBERS_BUYER, E.ORDERS_PRICE, E.ORDERS_DATE, E.ORDERS_DIV, E.SELLINGORDER_NO, E.MEMBERS_SELLER, E.ORDERS_STATUS, E.BUYING_ORDER_CODE, P.PRODUCT_CODE, P.PRODUCT_NAME FROM ORDERS E LEFT JOIN SELLING_ORDERS S ON E.SELLINGORDER_NO = S.SELLINGORDER_NO LEFT JOIN BUYING_ORDERS Y ON E.BUYING_ORDER_CODE = Y.BUYING_ORDER_CODE LEFT JOIN PRODUCT_OPTION H ON H.PRODUCT_OPTION_CODE = Y.PRODUCT_OPTION_CODE JOIN PRODUCT P ON H.PRODUCT_CODE = P.PRODUCT_CODE
E.MEMBERS_BUYER = #{searchValue}
E.MEMBERS_SELLER = #{searchValue} AND E.ORDERS_DIV LIKE '%' '판매' '%'
P.PRODUCT_NAME LIKE '%' || #{searchValue} || '%'
E.ORDERS_NO = #{searchValue}
ORDER BY E.ORDERS_NO DESC ) D
WHERE ROWNUM <= #{endRow}
) A WHERE A.RNUM >= #{startRow} ORDER BY 1 DESC

SELECT COUNT(*) FROM ORDERS O JOIN SELLING_ORDERS S ON(S.SELLINGORDER_NO = O.SELLINGORDER_NO) JOIN BUYING_ORDERS B ON (B.BUYING_ORDER_CODE = O.BUYING_ORDER_CODE) JOIN PRODUCT_OPTION T ON (T.PRODUCT_OPTION_CODE = B.BUYING_ORDER_CODE) JOIN PRODUCT P ON (T.PRODUCT_CODE = P.PRODUCT_CODE)
P.PRODUCT_NAME LIKE '%' || #{searchValue} || '%'
O.ORDERS_NO = #{ searchValue}
B.MEMBERS_NO = #{ searchValue}
S.MEMBERS_NO = #{ searchValue }
